/* ABOUT DRESSES LENGTH 2 choices maximum*/
$(function(){
  $("input.limit-two-dress").change(function(){
      limit=2;
      nb=$("input.limit-two-dress:checked").length;
      if(nb>limit){
        $(this).attr("checked", false);
      }
  });

});
